import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Gap, InputForm } from '../../Component';
import { styContainer, styTitle, styContainerForm } from './styles';

const InventoryForm = () => {
  let { id } = useParams();

  const [data, setData] = useState([]);

  const [sendData, setSendData] = useState({
    sku: '',
    name: '',
    costPrice: '',
    retailPrice: '',
    qty: '',
    marginPercentage: '',
  });

  useEffect(() => {
    setData([]);
    axios
      .get(
        `https://mobile.dev.quadrant-si.id/developertest/InventoryItem/${id}`
      )
      .then((res) => {
        setData(res.data);
      });
  }, [id]);

  return (
    <div className={styContainer}>
      <div className={styTitle}>Update Inventory</div>
      <form className={styContainerForm}>
        <InputForm label="SKU" value={data.sku} onChange />
        <Gap height={12} />
        <InputForm label="Name" value={data.name} />
        <Gap height={12} />
        <InputForm label="Cost Price" value={data.costPrice} />
        <Gap height={12} />
        <InputForm label="Retail Price" value={data.retailPrice} />
        <Gap height={12} />
        <InputForm label="Qty" value={data.qty} />
        <Gap height={12} />
        <InputForm label="Margin Percentage" value={data.marginPercentage} />
      </form>
    </div>
  );
};

export default InventoryForm;
