import React from 'react';
import { Link } from 'react-router-dom';
import { IcAdd } from '../../Assets';
import { styBtnNavigation, styContainerBtn, styBtnText } from './style';

const Button = ({ text, link, type, onClick }) => {
  switch (type) {
    case 'link':
      return (
        <Link className={styBtnNavigation} to={link}>
          {text}
        </Link>
      );
    case 'btn-plus':
      return (
        <div className={styContainerBtn} onClick={onClick}>
          <img height={35} src={IcAdd} alt="Ic-add" />
        </div>
      );
    case 'btn-text':
      return (
        <div className={styBtnText} onClick={onClick}>
          {text}
        </div>
      );

    default:
      break;
  }
};

export default Button;
