import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

import {
  Button,
  Gap,
  HeaderNavigation,
  InputForm,
  Dropdown,
} from '../../Component';
import { styContainer, styTitle, styContainerForm } from './styles';

const SupplierForm = () => {
  const navigate = useNavigate();
  const [data, setData] = useState({
    name: '',
    address: '',
    city: '',
    postCode: '',
  });

  const onHandleSubmit = (e) => {
    e.preventDefault();
    axios
      .put(
        `https://mobile.dev.quadrant-si.id/developertest/InventoryItem/UpdateItem`,
        data
      )
      .then((response) => {
        navigate(`/inventory`, { replace: true });
      });
  };

  return (
    <div className={styContainer}>
      <HeaderNavigation firstLink={-1} />
      <Gap height={50} />
      <div className={styTitle}>Tambah Inventory</div>
      <form className={styContainerForm} onSubmit={onHandleSubmit}>
        <Gap height={12} />
        <InputForm
          label="Name"
          value={data.name}
          onChange={(e) => {
            setData({ ...data, name: e });
          }}
        />
        <Gap height={12} />
        <InputForm
          label="Cost Price"
          value={data.address}
          onChange={(e) => {
            setData({ ...data, address: e });
          }}
        />
        <Gap height={12} />
        <InputForm
          label="Retail Price"
          value={data.city}
          onChange={(e) => {
            setData({ ...data, city: e });
          }}
        />
        <Gap height={12} />
        <InputForm
          label="Name"
          value={data.postCode}
          onChange={(e) => {
            setData({ ...data, postCode: e });
          }}
        />
        <Gap height={12} />

        <Gap height={14} />
        <Button type="btn-submit" text="Submit" />
      </form>
    </div>
  );
};

export default SupplierForm;
