import React from 'react';

import { stySelect, styLabel, styDropdownWrapper } from './styles';

const Dropdown = ({ label, data, onChange }) => {
  return (
    <div className={styDropdownWrapper}>
      <div className={styLabel}>{label}</div>
      <select className={stySelect}>
        <option value="option 1" disabled>
          Pilih Supplier
        </option>
        {data.map((e, i) => (
          <option key={i} value={e.id}>
            {e.name}
          </option>
        ))}
      </select>
    </div>
  );
};

export default Dropdown;
