import React from 'react';
import { Routes as Switch, Route, useLocation } from 'react-router-dom';
import { ButtonNavigation } from '../Component';
import { FormSupplier, Home, Inventory } from '../Pages';
import {
  styContainer,
  styMobileContainer,
  styMobileView,
  styTitle,
} from './styles';

const Routes = () => {
  let location = useLocation();

  return (
    <Switch location={location} key={location.pathname}>
      <Route path="/" element={<Home />} />
      <Route path="/supplier" element={<Inventory />} />
      <Route path="/supplier/add" element={<FormSupplier />} />
      <React.Fragment>
        <div className={styContainer}>
          <div className={styMobileView}>
            <div className={styMobileContainer}>
              <h1 className={styTitle}>Inventory Management</h1>
            </div>
            <ButtonNavigation />
          </div>
        </div>
      </React.Fragment>
    </Switch>
  );
};

export default Routes;
