import { css } from '@emotion/css';

export const styBtnNavigation = css`
  text-decoration: none;
  color: #000;
  font-weight: 500;
  :hover {
    color: blue;
  }
`;

export const styBtnNavigationActive = css`
  text-decoration: none;
  color: blue;
  font-weight: 500;
  :hover {
    color: darkBlue;
  }
`;

export const styContainerBtn = css`
  width: 50px;
  height: 50px;
  background-color: blue;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 5px;
  cursor: pointer;
`;

export const styBtnText = css`
  cursor: pointer;
  font-weight: 600;
  :hover {
    color: blue;
  }
`;

export const styBtnSubmit = css`
  height: 45px;
  width: 90%;
  background-color: blue;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 20px;
  color: white;
`;
