import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Button, Gap, InputForm } from '../../Component';
import { styContainer, styTitle, styContainerForm } from './styles';

const InventoryForm = () => {
  let { id } = useParams();

  const [data, setData] = useState({});

  useEffect(() => {
    setData([]);
    axios
      .get(
        `https://mobile.dev.quadrant-si.id/developertest/InventoryItem/${id}`
      )
      .then((res) => {
        setData(res.data);
      })
      .catch((error) => console.log(error));
  }, [id]);

  const onHandleSubmit = (e) => {
    e.preventDefault();
    console.log(data);
  };

  return (
    <div className={styContainer}>
      <div className={styTitle}>Update Inventory</div>
      <form className={styContainerForm} onSubmit={onHandleSubmit}>
        <InputForm
          label="SKU"
          value={data.sku}
          onChange={(e) => {
            setData({ ...data, sku: e });
          }}
        />
        <Gap height={12} />
        <InputForm
          label="Name"
          value={data.name}
          onChange={(e) => {
            setData({ ...data, name: e });
          }}
        />
        <Gap height={12} />
        <InputForm
          label="Cost Price"
          value={data.costPrice}
          onChange={(e) => {
            setData({ ...data, costPrice: e });
          }}
          number
        />
        <Gap height={12} />
        <InputForm
          label="Retail Price"
          value={data.retailPrice}
          onChange={(e) => {
            setData({ ...data, retailPrice: e });
          }}
          number
        />
        <Gap height={12} />
        <InputForm
          label="Qty"
          value={String(data.qty)}
          onChange={(e) => {
            setData({ ...data, qty: e });
          }}
          number
        />
        <Gap height={12} />
        <InputForm
          value={String(data.marginPercentage)}
          label="Margin Percentage"
          onChange={(e) => {
            setData({ ...data, marginPercentage: e });
          }}
          number
        />
        <Gap height={14} />
        <Button type="btn-submit" text="Submit" />
      </form>
    </div>
  );
};

export default InventoryForm;
