import { css } from '@emotion/css';

export const styContainer = css`
  border: 1px solid #808080;
  padding: 10px;
  border-radius: 5px;
  display: block;
  text-decoration: none;
  cursor: pointer;
  :hover {
    border: 2px solid blue;
  }
`;

export const styNameSupplier = css`
  font-size: 23px;
  font-weight: 600;
  color: #000;
`;

export const styAddress = css`
  color: #808080;
  font-size: 13px;
`;
