import React from 'react';
import { Routes as Switch, Route, useLocation } from 'react-router-dom';
import { ButtonNavigation } from '../Component';
import { FormSupplier, Home, Inventory, InventoryDetail } from '../Pages';
import {
  styContainer,
  styMobileContainer,
  styMobileView,
  styTitle,
} from './styles';

const Routes = () => {
  let location = useLocation();

  return (
    <React.Fragment>
      <div className={styContainer}>
        <div className={styMobileView}>
          <Switch location={location} key={location.pathname}>
            <Route path="/" element={<Home />} />
            <Route path="/inventory" element={<Inventory />} />
            <Route path="/supplier/add" element={<FormSupplier />} />
            <Route path="/inventory/detail/:id" element={<InventoryDetail />} />
          </Switch>
        </div>
      </div>
    </React.Fragment>
  );
};

export default Routes;
