import { css } from '@emotion/css';

export const styDropdownWrapper = css`
  display: flex;
  flex-flow: column;
  justify-content: flex-start;
`;

export const stySelect = css`
  width: 90%;
  appearance: none;
  background-color: transparent;
  border: none;
  padding: 0 1em 0 0;
  margin: 0;
  font-family: inherit;
  font-size: inherit;
  cursor: inherit;
  line-height: inherit;
  ::-ms-expand {
   display: none;
 }
`;

export const styOption = css`
  color: 'black';
`;

export const styOptionSelected = css`
  background-color: DodgerBlue;
  &:after {
    position: absolute;
    content: '';
    top: 14px;
    right: 10px;
    width: 0;
    height: 0;
    border: 6px solid transparent;
    border-color: #fff transparent transparent transparent;
  }
`;

export const styLabel = css`
  margin-bottom: 1rem;
`;
