import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Gap, InputForm } from '../../Component';
import { styContainer, styTitle, styContainerForm } from './styles';

const InventoryForm = () => {
  let { id } = useParams();

  const [data, setData] = useState([]);

  useEffect(() => {
    setData([]);
    axios
      .get(
        `https://mobile.dev.quadrant-si.id/developertest/InventoryItem/${id}`
      )
      .then((res) => {
        setData(res.data);
      });
  }, [id]);

  return (
    <div className={styContainer}>
      <div className={styTitle}>Update Inventory</div>
      <div className={styContainerForm}>
        <InputForm label="SKU" value={data.sku} />
        <Gap height={12} />
        <InputForm label="Name" value={data.sku} />
        <Gap height={12} />
        <InputForm label="Cost Price" />
        <Gap height={12} />
        <InputForm label="Retail Price" />
        <Gap height={12} />
        <InputForm label="Qty" />
        <Gap height={12} />
        <InputForm label="Margin Percentage" />
      </div>
    </div>
  );
};

export default InventoryForm;
