import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { IcAdd } from '../../Assets';
import { styBtnNavigation } from './style';

const Button = ({ text, link, type }) => {
  switch (type) {
    case 'link':
      return (
        <Link className={styBtnNavigation} to={link}>
          {text}
        </Link>
      );
    case 'btn-plus':
      return (
        <div>
          <IcAdd />
        </div>
      );

    default:
      break;
  }
};

export default Button;
